package string;

public class String_Ex1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//문자형 형변환
		String st = "100";
		
		int num = Integer.parseInt(st);
		System.out.println(num);
		
		//숫자형 형변환
		int number = 123456;
		System.out.println(String.valueOf(number));

		
	}

}
